import React from 'react';

const INICIAR_STATE = {
  name: '',
  password: '',
  email: '',
  foto: '',
};

class EditPerfil extends React.Component {
  constructor() {
    super();
    this.state = INICIAR_STATE;

    this.onHandleChange = this.onHandleChange.bind(this);
  }

  componentDidMount() {
    const { nome } = this.props;
    const info = JSON.parse(localStorage.getItem(nome));
    if (info) {
      this.setState({ ...info });
    }
  }

  onHandleChange({ target }) {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  }

  salvar() {
    const { name } = this.state;
    localStorage.setItem(name, JSON.stringify(this.state));
  }

  render() {
    const { name, password, email, foto } = this.state;
    return (
      <form>
        <label htmlFor="name">
          <input
            type="text"
            onChange={ this.onHandleChange }
            value={ name }
            name="name"
            id="name"
          />
        </label>
        <label htmlFor="password">
          <input
            type="password"
            onChange={ this.onHandleChange }
            value={ password }
            name="password"
            id="password"
          />
        </label>
        <label htmlFor="email">
          <input
            type="email"
            onChange={ this.onHandleChange }
            value={ email }
            name="email"
            id="email"
          />
        </label>
        <label htmlFor="foto">
          <input
            type="text"
            onChange={ this.onHandleChange }
            value={ foto }
            name="foto"
            id="foto"
          />
        </label>
        <button type="button" onClick={ this.salvar }>Salvar</button>
      </form>
    );
  }
}

export default EditPerfil;
