import React from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header'

export default class Login extends React.Component {
    state = {
       userName: '',
       password: '',
       userNameStorage: '',
       passwordStorage: '',
    }



    handleChange = ({ target }) => {
        const { name, value } = target;
        this.setState({ [name]: value });
      };

    getLocalStorage = () => {
        const { userName} = this.state
        if(localStorage.getItem(userName)){
            const userNameStrg = JSON.parse(localStorage.getItem(userName))
            this.setState({ userNameStorage: userNameStrg.userName , passwordStorage: userNameStrg.password})
        }
    }  




    render() {
        const {userName,password} = this.state;
        return (
            <div>
                <Header userName={ userName }/>
                <h1>Login</h1>
                <form className="loginForm">
                    <label htmlFor="user-name">Usuario:
                        <input type="text" onChange={this.handleChange} name="userName" id="user-name" value={userName}></input>
                    </label>
                    <label htmlFor="password">Senha:
                        <input type="password" onChange={this.handleChange} name="password" id="password" value={password}></input>
                    </label>
                    <div>
                    <button type="button">Entrar</button>
                    <Link to="createaccount">
                    <button type="button">Criar conta</button>
                    </Link>
                    </div>
                </form>
            </div>

        )
    }
}
