import React from 'react';

const INICIAR_STATE = {
  userName: '',
  name: '',
  password: '',
  email: '',
  foto: '',
  info: [],
  userNameStorage: '',
  redirect: false
};

class EditPerfil extends React.Component {
  constructor() {
    super();
    this.state = INICIAR_STATE;

    this.onHandleChange = this.onHandleChange.bind(this);
    this.salvar = this.salvar.bind(this);
  }

  componentDidMount() {
    const { userName } = this.props;
    const info = JSON.parse(localStorage.getItem(userName));
    if (info) {
      this.setState({...info });
    }
  }

  getLocalStorage = () => {
    const { userName} = this.state
    if(localStorage.getItem(userName)){
        const userNameStrg = JSON.parse(localStorage.getItem(userName))
        this.setState({ userNameStorage: userNameStrg.userName })
    }
}  

  onHandleChange({ target }) {
    const { name, value } = target;
    this.setState({
      [name]: value,
    }, () => this.getLocalStorage());
  }

  salvar() {
    const { userName, userNameStorage, info } = this.state;
    if (userNameStorage === userName){
      alert("Já existe um usuario com esse nome")
    }
    else{
    this.setState({userName,name,email,password})
    localStorage.setItem(userName, JSON.stringify(this.state));
    }
  }

  render() {
    const { name, password, email, foto, userName } = this.state;
    return (
      <form>
          <label htmlFor="userName">
            Usuario:
          <input
            type="text"
            onChange={ this.onHandleChange }
            value={ userName }
            name="userName"
            id="userName"
          />
        </label>
        <label htmlFor="name">
          Nome:
          <input
            type="text"
            onChange={ this.onHandleChange }
            value={ name }
            name="name"
            id="name"
          />
        </label>
        <label htmlFor="password">
          Senha:
          <input
            type="password"
            onChange={ this.onHandleChange }
            value={ password }
            name="password"
            id="password"
          />
        </label>
        <label htmlFor="email">
          Email:
          <input
            type="email"
            onChange={ this.onHandleChange }
            value={ email }
            name="email"
            id="email"
          />
        </label>
        <label htmlFor="foto">
          Foto:
          <input
            type="text"
            onChange={ this.onHandleChange }
            value={ foto }
            name="foto"
            id="foto"
          />
        </label>
        <button type="button" onClick={ this.salvar }>Salvar</button>
      </form>
    );
  }
}

export default EditPerfil;
