import React from 'react';

const INICIAR_STATE = {
  userName: '',
  name: '',
  password: '',
  email: '',
  foto: '',
};

class EditPerfil extends React.Component {
  constructor() {
    super();
    this.state = INICIAR_STATE;

    this.onHandleChange = this.onHandleChange.bind(this);
    this.salvar = this.salvar.bind(this);
  }

  componentDidMount() {
    const { userName } = this.props;
    const info = JSON.parse(localStorage.getItem(userName));
    if (info) {
      this.setState({information: info, ...info });
    }
  }

  onHandleChange({ target }) {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  }

  salvar() {
    const { userName } = this.state;
    localStorage.setItem(userName, JSON.stringify(this.state));
  }

  render() {
    const { name, password, email, foto, userName } = this.state;
    return (
      <form>
          <label htmlFor="userName">
            Usuario:
          <input
            type="text"
            onChange={ this.onHandleChange }
            value={ userName }
            name="userName"
            id="userName"
          />
        </label>
        <label htmlFor="name">
          Nome:
          <input
            type="text"
            onChange={ this.onHandleChange }
            value={ name }
            name="name"
            id="name"
          />
        </label>
        <label htmlFor="password">
          Senha:
          <input
            type="password"
            onChange={ this.onHandleChange }
            value={ password }
            name="password"
            id="password"
          />
        </label>
        <label htmlFor="email">
          Email:
          <input
            type="email"
            onChange={ this.onHandleChange }
            value={ email }
            name="email"
            id="email"
          />
        </label>
        <label htmlFor="foto">
          Foto:
          <input
            type="text"
            onChange={ this.onHandleChange }
            value={ foto }
            name="foto"
            id="foto"
          />
        </label>
        <button type="button" onClick={ this.salvar }>Salvar</button>
      </form>
    );
  }
}

export default EditPerfil;
