import React from "react";
import { Link } from "react-router-dom";
import Header from "../components/Header";
import propTypes from "prop-types";
import NameImg from "../components/NameImg";


export default class Home extends React.Component {
  state = {
    info: [],
    textArea:'',
  };

  componentDidMount() {
    this.getLocalStorage();
  }

  getLocalStorage = () => {

    const {
      location: {
        state: { userName },
      },func
    } = this.props;
    const {location: { state: { user } } } = this.props;
    if(userName){
    func(userName) 
    const userNameStrg = JSON.parse(localStorage.getItem(userName));
    this.setState({ info: userNameStrg })}
    else{
    func(user) 
    const userNameStrg = JSON.parse(localStorage.getItem(user));
    this.setState({ info: userNameStrg })
    }
  }

  onHandleChange({ target }) {
    const { name, value } = target;
    this.setState({
      [name]: value,
    }, () => this.getLocalStorage());
  }

  render() {
    const {
      newUser
    } = this.props;
    const {info} = this.state;
    return (
      <div className="homePage">
          <Header user={newUser} />
        <div className="post"> 
         <NameImg info={info} />
        <form>
        <fieldset>
          <legend>Add a status</legend>
          <textarea name="textarea" id="textArea" cols="50" rows="3" onChange={this.handleChange}></textarea>
        </fieldset>
        <button type="button">img</button>
        </form>
        <div className="buttonPost">
        <button>Post</button>
        </div>
        </div>
      </div>
    );
  }
}

Home.propTypes = {
  location: propTypes.shape({
    state: propTypes.shape({
      cartProducts1: propTypes.oneOfType([propTypes.array]),
    }),
  }),
}.isRequired;
